components/LoadingSpinner.tsx.content:
import { cn } from "@/lib/utils";

interface LoadingSpinnerProps {
  size?: "sm" | "md" | "lg";
  message?: string;
  className?: string;
  showMessage?: boolean;
}

export function LoadingSpinner({
  size = "md",
  message = "Loading...",
  className,
  showMessage = true,
}: LoadingSpinnerProps) {
  const sizeClasses = {
    sm: "w-4 h-4 border-2",
    md: "w-6 h-6 border-2",
    lg: "w-8 h-8 border-[3px]",
  };

  const containerPadding = {
    sm: "min-h-[60px]",
    md: "min-h-[80px]",
    lg: "min-h-[100px]",
  };

  return (
    <div
      className={cn(
        "flex flex-col items-center justify-center",
        containerPadding[size],
        className
      )}
    >
      <div
        className={cn(
          "border-muted-foreground/20 border-t-primary rounded-full animate-spin",
          sizeClasses[size]
        )}
        role="status"
        aria-label="Loading"
      />
      {showMessage && (
        <p className="mt-3 text-sm text-muted-foreground animate-pulse">
          {message}
        </p>
      )}
    </div>
  );
}

export function InlineSpinner({
  size = "sm",
  className,
}: {
  size?: "sm" | "md" | "lg";
  className?: string;
}) {
  const sizeClasses = {
    sm: "w-4 h-4 border-2",
    md: "w-5 h-5 border-2",
    lg: "w-6 h-6 border-2",
  }; // ‚úÖ removed the stray colon

  return (
    <div
      className={cn(
        "border-muted-foreground/20 border-t-primary rounded-full animate-spin",
        sizeClasses[size],
        className
      )}
      role="status"
      aria-label="Loading"
    />
  );
}

components/NewChatDialog.tsx.content:
'use client';

import { Doc } from "@/convex/_generated/dataModel";
import { useCreateNewChat } from "@/hooks/useCreateNewChat";
import { useUser } from "@clerk/nextjs";
import { useState } from "react";
import { useChatContext } from "stream-chat-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog"
import UserSearch from "./UserSearch";
import Image from "next/image";
import { XIcon } from "lucide-react";
import { Input } from "./ui/input";
import { Button } from "./ui/button";


export function NewChatDialog({children}: {children: React.ReactNode}){
    const [open, setOpen] = useState(false);
    const [selectedUsers, setSelectedUsers] = useState<Doc<"users">[]>([]);
    const [groupName, setGroupName] = useState("");
    const createNewChat = useCreateNewChat ();
    const {user} = useUser();
    const {setActiveChannel} = useChatContext();

    const handleSelectUser = (user: Doc<"users">)=>{
        if(!selectedUsers.find((u)=> u._id === user._id)){
            setSelectedUsers((prev)=>[...prev, user]);
        }
    }

    const removeUser = (userId: string) => {
        setSelectedUsers((prev)=> prev.filter((user)=> user._id !== userId));
    };

    const handleOpenChange = (newOpen: boolean)=>{
        setOpen(newOpen);
        if(!newOpen){
            setSelectedUsers([]);
            setGroupName("");
        }
    };

    const handleCreateChat = async () =>{
        const totalMembers = selectedUsers.length + 1;
        const isGroupChat = totalMembers > 2;

        const channel = await createNewChat({
            members:[
                user?.id as string,
                ...selectedUsers.map((user) =>user.userId),
            ],
            createdBy: user?.id as string,
            groupName: isGroupChat ? groupName.trim() || undefined : undefined,
        });

        setActiveChannel(channel);

        setSelectedUsers([]);
        setGroupName("");
        setOpen(false);
    }

    return (
        <Dialog open={open}
        onOpenChange={handleOpenChange}>
            <DialogTrigger asChild>{children}</DialogTrigger>

            <DialogContent className="sm:max-w-[500px] max-h-[80vh] overflow-y-auto">
                <DialogHeader>
                    <DialogTitle>Start a New Chat</DialogTitle>
                    <DialogDescription>
                        Search for users to start a conversation with
                    </DialogDescription>
                </DialogHeader>

                <div className="space-y-4">
                    <UserSearch onSelectUser={handleSelectUser} className="w-full" />

                    {selectedUsers.length > 0 && (
                        <div className="space-y-3">
                            <h4 className="text-sm font-medium text-foreground">
                                Selected Users ({selectedUsers.length})
                            </h4>
                            <div className="space-y-2 max-h-[200px] overflow-y-auto">
                                {selectedUsers.map((user)=>(
                                    <div
                                    key={user._id}
                                    className="flex items-center justify-between p-2 bg-muted/50 border border-border rounded-lg"
                                    >
                                        <div className="flex items-center space-x-2">
                                            <Image 
                                            src={user.imageUrl}
                                            alt={user.name}
                                            width={24}
                                            height={24}
                                            className="h-6 w-6 rounded-full object-cover" />

                                            <div className="min-w-0 flex-1">
                                                <p className="text-sm font-medium text-foreground truncate">
                                                    {user.name}
                                                </p>
                                                <p className="text-xs text-muted-foreground truncate">
                                                    {user.email}
                                                </p>
                                            </div>
                                        </div>
                                        <button
                                        onClick={()=>removeUser(user._id)}
                                        className="text-muted-foreground hover:text-destructive transition-colors p-1"  >
                                            <XIcon className="h-4 w-4"/>
                                            </button>
                                    </div>
                                ))}
                            </div>

                            {selectedUsers.length > 1 && (
                                <div className="space-y-2">
                                    <label 
                                    htmlFor="groupName"
                                    className="text-sm font-medium text-foreground" >
                                        Group Name (Optional)
                                    </label>
                                    <Input 
                                    id="groupName"
                                    type="text"
                                    placeholder="Enter a name for your group chat..."
                                    value={groupName}
                                    onChange={(e)=> setGroupName(e.target.value)}
                                    className="w-full" />

                                    <p className="text-xs text-muted-foreground">
                                        Leave empty to use default name: &quot;Group chat (
                                            {selectedUsers.length + 1} members
                                        )&quot;
                                    </p>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                <DialogFooter>
                    <Button 
                    variant="outline"
                    onClick={()=> setOpen(false)} >
                        Cancel
                    </Button>
                    <Button 
                    disabled={selectedUsers.length === 0}
                    onClick={handleCreateChat} >
                        {selectedUsers.length > 1 
                        ? `Create Group Chat (${selectedUsers.length + 1} members)`
                            : selectedUsers.length === 1
                            ? "Start Chat"
                        :  "Create Chat" }
                    </Button>
                </DialogFooter>
            </DialogContent>
        </Dialog>
    ); 
}

