'use client';

import { createToken } from '@/actions/createToken';
import { InlineSpinner } from '@/components/LoadingSpinner';
import { StatusCard } from '@/components/StatusCard';
import { useUser } from '@clerk/nextjs';
import {
  Call,
  CallingState,
  StreamCall,
  StreamTheme,
  StreamVideo,
  StreamVideoClient,
} from '@stream-io/video-react-sdk';
import { AlertTriangle, Video } from 'lucide-react';
import { useParams } from 'next/navigation';
import { useCallback, useEffect, useMemo, useState } from 'react';
import "@stream-io/video-react-sdk/dist/css/styles.css";

if (!process.env.NEXT_PUBLIC_STREAM_API_KEY) {
  throw new Error('NEXT_PUBLIC_STREAM_API_KEY is not set');
}

function Layout({ children }: { children: React.ReactNode }) {
  const { user } = useUser(); // ‚úÖ must call hook
  const { id } = useParams(); // ‚úÖ must call hook
  const [call, setCall] = useState<Call | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [client, setClient] = useState<StreamVideoClient | null>(null);

  const streamUser = useMemo(() => {
    if (!user?.id) return null;

    return {
      id: user.id,
      name:
        user.fullName ||
        user.emailAddresses[0]?.emailAddress ||
        'Unknown User',
      image: user.imageUrl || '',
      type: 'authenticated' as const,
    };
  }, [user]);

  const tokenProvider = useCallback(async () => {
    if (!user?.id) {
      throw new Error('User not authenticated');
    }
    return await createToken(user.id);
  }, [user?.id]);

  useEffect(() => {
    if (!streamUser) {
      setClient(null);
      return;
    }

    const newClient = new StreamVideoClient({
      apiKey: process.env.NEXT_PUBLIC_STREAM_API_KEY as string,
      user: streamUser,
      tokenProvider,
    });

    setClient(newClient);

    return () => {
      newClient.disconnectUser().catch(console.error);
    };
  }, [streamUser, tokenProvider]);

  useEffect(() => {
    if (!client || !id) return;

    setError(null);
    const callInstance = client.call('default', id as string); // ‚úÖ renamed

    const joinCall = async () => {
      try {
        await callInstance.join({ create: true });
        setCall(callInstance);
      } catch (error) {
        console.error('Failed to join call:', error);
        setError(
          error instanceof Error ? error.message : 'Failed to join call'
        );
      }
    };

    joinCall();

    return () => {
      if (
        callInstance &&
        callInstance.state.callingState === CallingState.JOINED
      ) {
        callInstance.leave().catch(console.error);
      }
    };
  }, [id, client]);

  if(error){
    return(
    <StatusCard
    title='Call Error'
    description={error}
    className='min-h-screen bg-red-50'
    action={
        <button
        onClick={()=> window.location.reload()}
        className='w-full bg-red-600 hover:bg-red-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2' >
            Retry
        </button>
    } 
    >
        <div className='w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto'>
            <AlertTriangle className='w-8 h-8 text-red-600' />
        </div>

    </StatusCard>
    );
  };

  if (!client) {
    return (
        <StatusCard
        title='Initializing client...'
        description="Setting up video call connection..."
        className='min-h-screen bg-blue-50' >
            <InlineSpinner size='lg' />
        </StatusCard>
    );
  }

  if (!call) {
    return (
        <StatusCard title='Joining call...' className='min-h-screen bg-green-50' >
            <div className='animate-bounce h-16 w-16 mx-auto'>
                <div className='w-16 h-16 bg-green-200 rounded-full flex items-center justify-center'>
                    <Video className='w-8 h-8 text-green-600' />
                </div>
            </div>
            <div className='text-green-600 font-mono text-sm bg-green-100 px-3 py-1 rounded-full inline-block'>
                Call ID: {id}
            </div>
        </StatusCard>
    );
  }

  if (error) {
    return <div className="text-red-500">Error: {error}</div>;
  }

  return (
    <StreamVideo client={client}>
      <StreamTheme className="text-white">
        <StreamCall call={call}>{children}</StreamCall>
      </StreamTheme>
    </StreamVideo>
  );
}

export default Layout;

"use client";

import { InlineSpinner } from "@/components/LoadingSpinner";
import { StatusCard } from "@/components/StatusCard";
import { useSidebar } from "@/components/ui/sidebar";
import {
  CallControls,
  CallingState,
  SpeakerLayout,
   // ‚úÖ correct import
  useCallStateHooks,
} from "@stream-io/video-react-sdk";
import { Check, Copy } from "lucide-react";
import { useRouter } from "next/navigation"; // ‚úÖ App Router
import { useState } from "react";

function VideoCall() {
  const { useCallCallingState, useParticipants } = useCallStateHooks();
  const callingState = useCallCallingState();
  const participants = useParticipants();
  const router = useRouter();
  const [copied, setCopied] = useState(false);
  const { setOpen } = useSidebar();

  const handleLeave = () => {
    router.push("/dashboard");
    setOpen(true);
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(window.location.href);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error("Failed to copy:", error);
    }
  };

  if (callingState === CallingState.JOINING) {
    return (
      <StatusCard
        title="Joining call..."
        description="Please wait while we connect you to the call."
        className="bg-gray-50 rounded-lg"
      >
        <InlineSpinner size="lg" />
      </StatusCard>
    );
  }

  if (callingState === CallingState.RECONNECTING) {
    return (
      <StatusCard
        title="Reconnecting..."
        description="Connection lost, attempting to reconnect."
        className="bg-yellow-50 rounded-lg border border-yellow-200"
      >
        <div className="animate-pulse rounded-full h-12 w-12 bg-yellow-400 mx-auto"></div>
      </StatusCard>
    );
  }

  if (callingState !== CallingState.JOINED) {
    return (
      <StatusCard
        title="Loading call..."
        description={`Status: ${callingState}`}
        className="bg-gray-50 rounded-lg"
      >
        <div className="animate-pulse rounded-full h-12 w-12 bg-gray-400 mx-auto"></div>
      </StatusCard>
    );
  }

  return (
    <div className="flex flex-col">
      <div className="flex-1 relative">
        <SpeakerLayout />
      </div>
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 z-10">
        <CallControls onLeave={handleLeave} />
      </div>

      {participants.length === 1 && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/50 backdrop-blur-sm">
            <div className="bg-white rounded-2xl p-8 max-w-lg w-full mx-4 shadow-2xl">
                <div className="text-center space-y-6">
                    <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto">
                        <Copy className="w-8 h-8 text-blue-600" />
                    </div>

                    <div className="space-y-2">
                        <h2 className="text-2xl font-bold text-gray-900">
                            Waiting for others to join
                        </h2>
                        <p className="text-gray-600">
                            Share this link with others to invite them to the call
                        </p>
                    </div>

                    <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
                        <div className="flex items-center gap-3">
                            <div className="flex-1 text-sm text-gray-700 font-mono break-all">
                                {window.location.href}
                            </div>
                            <button 
                            onClick={copyToClipboard}
                            className=" flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 whitespace-nowrap" >
                                {copied ? (
                                    <>
                                      <Check className="w-4 h-4" />
                                      Copied!
                                    </>
                                ):(
                                    <>
                                      <Copy className="w-4 h-4" />
                                      Copy Link
                                    </>
                                )}
                            </button>
                        </div>
                    </div>

                    <p className="text-sm text-gray-500">Others will be able to join using this link</p>
                </div>
            </div>
        </div>
      )}
    </div>
  );
}

export default VideoCall;
